select
id, account, user_name, user_password, avatar_url, role_code, create_time, login_time
from user_info


        and id = #{id}


        and account = #{account}


        and user_name = #{userName}


        and user_password = #{userPassword}


        and avatar_url = #{avatarUrl}


        and role_code = #{roleCode}


        and create_time = #{createTime}


        and login_time = #{loginTime}


limit #{pageable.offset}, #{pageable.pageSize}

select id, account, user_name, user_password, avatar_url, role_code, create_time, login_time from blog.user_info
select count(1) from user_info
and id = #{id}
and account = #{account}
and user_name = #{userName}
and user_password = #{userPassword}
and avatar_url = #{avatarUrl}
and role_code = #{roleCode}
and create_time = #{createTime}
and login_time = #{loginTime}
insert into user_info(account, user_name, user_password, avatar_url, role_code, create_time, login_time) values (#{account}, #{userName}, #{userPassword}, #{avatarUrl}, #{roleCode}, #{createTime}, #{loginTime})
insert into user_info(account, user_name, user_password, avatar_url, role_code, create_time, login_time) values
(#{entity.account}, #{entity.userName}, #{entity.userPassword}, #{entity.avatarUrl}, #{entity.roleCode}, #{entity.createTime}, #{entity.loginTime})
insert into user_info(account, user_name, user_password, avatar_url, role_code, create_time, login_time) values
(#{entity.account}, #{entity.userName}, #{entity.userPassword}, #{entity.avatarUrl}, #{entity.roleCode}, #{entity.createTime}, #{entity.loginTime})
on duplicate key update account = values(account), user_name = values(user_name), user_password = values(user_password), avatar_url = values(avatar_url), role_code = values(role_code), create_time = values(create_time), login_time = values(login_time)
update user_info
account = #{account},
user_name = #{userName},
user_password = #{userPassword},
avatar_url = #{avatarUrl},
role_code = #{roleCode},
create_time = #{createTime},
login_time = #{loginTime},
where id = #{id}
delete from user_info where id = #{id}
